pragma solidity ^0.4.24;
/**
  * @title   Individual Certification Contract
  * @author  Rosen GmbH
  *
  * This contract represents the individual certificate.
  */
contract IndividualCertification {
  event HashValueChanged(string oldHashValue);
  address public certifierAddress;
  string public hashValue;

  modifier isSha512Hash(string memory _hashValue) {
    bytes memory stringTest = bytes(_hashValue);
    require(stringTest.length == 128);
    _;
  }

  constructor(string memory _hashValue)
    public
    isSha512Hash(_hashValue)
  {
    certifierAddress = msg.sender;
    hashValue = _hashValue;
  }

  function setHashValue(string memory _hashValue)
    public
    isSha512Hash(_hashValue)
  {
    require(msg.sender == certifierAddress);
    emit HashValueChanged(hashValue);
    hashValue = _hashValue;
  }
  /**
    * Extinguish this certificate.
    *
    * This can be done the same certifier contract which has created
    * the certificate in the first place only.
    */
  function deleteCertificate() public {
      require(msg.sender == certifierAddress);
      selfdestruct(tx.origin);
  }

}

