pragma solidity ^0.4.24;
/**
  * @title   Individual Certification Contract
  * @author  Rosen GmbH
  *
  * This contract represents the individual certificate.
  */
contract IndividualCertificationBinary {
  address public certifierAddress;
  bytes32 public first32bytesOfHashValue;
  bytes32 public last32bytesOfHashValue;

  constructor(bytes32 b0, bytes32 b1)
    public
  {
    certifierAddress = msg.sender;
    first32bytesOfHashValue = b0;
    last32bytesOfHashValue = b1;
  }

  function setHashValue(bytes32 b0, bytes32 b1)
    public
  {
    require(msg.sender == certifierAddress);
    first32bytesOfHashValue = b0;
    last32bytesOfHashValue = b1;
  }
}

